/* eslint-disable eqeqeq */
import React, { useState } from 'react'
import {Empty, Card,Image, Pagination} from 'antd'

export default function MainProduct({productDatas, addToCart}) {
    const pagesize = 6
    const [from, setFrom] = useState(0)
    const data = [...productDatas]
    const handlePagination = (value)=>{
        setFrom(pagesize*(value-1))
    }
    return (
        <div className="w-100">
            <div className="row">
            {
                data === []?<Empty/>:
                data.splice(from, pagesize).map((prod)=>(
                <div key={prod.id} className="col-md-4 my-2 ">
                    <Card
                        className="prod-card"
                        style={{minHeight:'400px'}}
                        hoverable
                        cover={
                            <div className="prod-img">
                                <Image
                                    alt={prod.imgAlt}
                                    src={prod.imgSrc}
                                    width="100%"
                                    style={{minHeight:'250px', maxHeight:'250px'}}
                                    fallback="data:image/png;base64,"
                                />
                                <div className={`p-label ${prod.bestseller?'d-block':'d-none'}`}>
                                    <p className="px-3 px-md-4 py-1 py-md-2 fw-bold bg-light">Best Seller</p>
                                </div>
                                {/* web display  */}
                                <div className="d-none d-md-block ">
                                    <div className="atc w-100">
                                        <button onClick={()=> addToCart(prod)} className="w-100 btn btn-dark rounded-0">ADD TO CART</button>
                                    </div>
                                </div>
                                {/* mobile display */}
                                <div className="d-block d-md-none ">
                                    <div className="atcm w-100">
                                        <button onClick={()=> addToCart(prod)} className="w-100 btn btn-dark rounded-0">ADD TO CART</button>
                                    </div>
                                </div>
                            </div>
                        }
                    >
                        <b className="fw-bold text-secondary">{prod.category}</b>
                        <Card.Meta
                        title={(<b>{prod.name}</b>)}
                        description={`${prod.currency =='USD'?'$':''}${prod.price}`}
                        />
                    </Card>

                </div>
            ))
            }
            </div>

            {/* pagination button  */}
            <div className="my-4 text-center">
            <Pagination onChange={handlePagination} defaultCurrent={1} total={productDatas.length} pageSize={pagesize}/>
            </div>
        </div>
    )
}
